package Com.mkpits.Datatypes;

public class BooleanDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // Boolean - Pre-defind/Primitive data type which includes in Logical data type.
		//Implementation
		boolean isPresent = true;
		boolean isNotPresent = false;
		boolean isActive = false;
		// Default value for boolean data type is false
		System.out.println(isPresent);
		System.out.println(isNotPresent);
		System.out.println(isActive);
	}

}
