package Com.mkpits.Datatypes;

public class FloatingDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Primitive/Pre-defind data type
        // Floating data type - float,double
		// Implementation
		float floatDataType = 12.5f;
		// it takes 4 byte of memory
		// explicit with the notation of f/F
		// append with the letter f/F
		// default value for float is 0.0f
		// this data type used to store floating values(decimal values)
		double doubleDataType = 454.542;
		// it take 8 byte of memory
		// default value for double is 0.0d
		// used to store huge decimal values as well as integer values
		System.out.println(floatDataType);
		System.out.println(doubleDataType );
	}

}
